const Table = (props)=>{
  const sorting = props.sorting;
  const sortedData = props.sortedData;


return <>
  <table>
    <thead>
      <th
        onClick={() => {
          sorting("transactionDate");
        }}
      >
        Transaction Date
      </th>
      <th
        onClick={() => {
          sorting("monthYear");
        }}
      >
        Month Year
      </th>
      <th
        onClick={() => {
          sorting("transactionType");
        }}
      >
        Transaction Type
      </th>
      <th
        onClick={() => {
          sorting("fromAccount");
        }}
      >
        From Account
      </th>
      <th
        onClick={() => {
          sorting("toAccount");
        }}
      >
        To Account
      </th>
      <th
        onClick={() => {
          sorting("transactionAmount");
        }}
      >
        Amount
      </th>
      <th>Receipt</th>
      <th
        onClick={() => {
          sorting("notes");
        }}
      >
        Notes
      </th>
      <th>Action</th>
    </thead>
    <tbody>
      {sortedData.map((transaction,index) => (
        <tr>
          <td>{transaction.transactionDate.value}</td>
          <td>{transaction.monthYear.value}</td>
          <td>{transaction.transactionType.value}</td>
          <td>{transaction.fromAccount.value}</td>
          <td>{transaction.toAccount.value}</td>
          <td>
            {Intl.NumberFormat("en-IN", {
              style: "currency",
              currency: "INR",
            }).format(transaction.transactionAmount.value)}
          </td>
          <td>
            <img
              src={transaction.receipt.value}
              width={50}
              height={50}
              alt=""
            />
          </td>
          <td>{transaction.notes.value}</td>
          <td>
            <a href="#" key={index}>View</a>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
</>;
}

export default Table;
