import "./Registration.css";

import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";

const Login = () => {
  const registrationData = JSON.parse(localStorage.getItem("registration"));

  const navigate = useNavigate();
  const [loginData, setRegData] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState({});
  const [issubmit, setIsSubmit] = useState(false);

  const hasChange = (e) => {
    const { name, value } = e.target;

    setRegData((prev) => {
      return { ...prev, [name]: value };
    });
    console.log(loginData);

    setError(validate(loginData));
  };

  const validate = (loginData) => {
    const error = {};
    console.log(loginData);
    const regex = /^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$/;

    if (!regex.test(loginData["email"])) {
      error.email = "email is required";
    }

    if (loginData["password"].length <= 4) {
      error.password = "password is required";
    } else {
      for (const key in registrationData) {
        if (
          registrationData[key].email !== loginData.email ||
          registrationData[key].password !== loginData.password
        ) {
          error.login = "email & password is not correct";
          // break;
        }
      }
    }
    return error;
  };
  console.log(error,"erorrro")
  console.log("length", Object.keys(error).length);

  const submitHandler = (e) => {
    e.preventDefault();
    hasChange(e);
    console.log(loginData);
    setIsSubmit(true);


  };

  useEffect(() => {
    if (Object.keys(error).length === 0 && issubmit === true) {
      console.log("keyyyyyyyyyyyyyyy")
      let flag = false;
      for (const key in registrationData) {
        if (
          registrationData[key].email === loginData.email ||
          registrationData[key].password === loginData.password
        ) {
          flag = true;
          error.login = "";
          break;
        }
      }

      if (flag === true) {
        let result = "";
        const characters =
          "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        const charactersLength = characters.length;
        let counter = 0;
        while (counter < 17) {
          result += characters.charAt(
            Math.floor(Math.random() * charactersLength)
          );
          counter += 1;
        }

        loginData["token"] = result;

        localStorage.setItem("logindata", JSON.stringify(loginData));

        navigate("/transaction");
      }
    }

  }, [error]);
  return (
    <>
      <div className="form">
        <h2>Login Form</h2>
        <form onSubmit={submitHandler} method="POST">
          <label className="label">Email:</label>
          <input
            type="email"
            name="email"
            className="inputFields"
            value={loginData.email}
            onChange={(e) => hasChange(e)}
          ></input>
          <span>{error.email}</span>
          <br></br>
          <label className="label">Password:</label>
          <input
            type="password"
            name="password"
            className="pswFields"
            value={loginData.password}
            onChange={(e) => hasChange(e)}
          ></input>
          <span>{error.password}</span>
          <span>{error.login}</span>
          <br></br>
          <button type="submit" className="ViewBtn">
            Submit
          </button>
          <br></br>
        </form>
        <Link to={"/"} className="loginBtn">
          Registration
        </Link>
      </div>
    </>
  );
};

export default Login;
