import "../assets/styles/transaction.css";
import React, { useState } from "react";
import { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { MonthArr, TransactionTypeArr, AccountArr } from "../utils/constant";
import { Dropdown } from "./Dropdown";

import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { useForm } from "react-hook-form";

const TransactionForm = () => {
  const { id } = useParams();
  const index = id - 1;
  const updateData = JSON.parse(localStorage.getItem("transactionForm"));
  // const formDetails = {

  //   monthYear: {
  //     value: "",
  //   },
  //   transactionDate: {
  //     value: "",
  //   },
  //   transactionType: {
  //     value: "",
  //   },
  //   fromAccount: {
  //     value: "",
  //   },
  //   toAccount: {
  //     value: "",
  //   },
  //   transactionAmount: {
  //     value: "",
  //   },
  //   receipt: {
  //     value: "",
  //   },
  //   notes: {
  //     value: "",
  //   },
  // };

  const [data, setData] = useState({});
  // const [isDate, setDate] = useState(false);
  // const [isMonth, setMonth] = useState(false);
  // const [isAmount, setAmount] = useState(false);
  // const [isFromAcc, setFromAcc] = useState(false);
  // const [isToAcc, setToAcc] = useState(false);
  // const [isNote, setNote] = useState(false);
  // const [isReceipt, setReceipt] = useState(false);
  // const [isType, setType] = useState(false);

  const navigate = useNavigate();

  // YUP VALIDATIONS...
  const MAX_FILE_SIZE = 10240; //100KB

  const validFileExtensions = {
    image: ["jpg", "gif", "png", "jpeg", "svg", "webp"],
  };

  const type = ["image/jpeg", "image/png", "image/jpg", "image/svg"];

  // function isValidFileType(fileName, fileType) {
  //   return (
  //     fileName &&
  //     validFileExtensions[fileType].indexOf(fileName.split(".").pop()) > -1
  //   );
  // }
  const formSchema = yup.object().shape({
    transactionDate: yup.string().required("Transaction Date is required!!"),
    monthYear: yup.string().required("month year is required!!"),
    transactionType: yup.string().required("transaction type is required!!"),
    fromAccount: yup
      .string()
      // .notOneOf([yup.ref("toAccount"), null], "To Account is  matched!!")
      .required("account is required!!"),
    toAccount: yup
      .string()
      .notOneOf([yup.ref("fromAccount"), null], "From Account is  matched!!")
      .required("account is required!!"),
    transactionAmount: yup
      .number()
      .integer()
      .positive()
      .min(1)
      .required()
      .typeError("amount is required!!"),
    notes: yup
      .string()
      .min(3)
      .max(250)
      .required()
      .typeError("notes is required!!"),
    receipt: yup.mixed().test({
      name: "is-sku",
      skipAbsent: true,
      test(value, error) {
        if (value === undefined || value === null || value.length === 0) {
          return error.createError({ message: "image is required!!!" });
        } else {

         console.log(type.includes(value[0].type));
          if ((!type.includes(value[0].type))) {
            return error.createError({
              message: "image type must be jpeg,png,jpg or svg..",
            });
          }
          if (value[0]["size"] > MAX_FILE_SIZE) {
            return error.createError({ message: "image must less than 10kb" });
          }
        }

        return true;
      },
    }),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  // ......

  useEffect(() => {
    for (const key in updateData) {
      if (key && parseInt(updateData[key]?.id) === parseInt(id)) {
        setData(updateData[key]);
        break;
      }
    }
    //eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const removeImage = () => {
    setData((prev) => ({
      ...prev,
      receipt: {
        ...prev.receipt,
        value: "",
      },
    }));
  };

  const onSubmit = (e) => {
    //  console.log(e)
    let {
      monthYear,
      transactionDate,
      transactionType,
      fromAccount,
      toAccount,
      transactionAmount,
      receipt,
      notes,
    } = e;
    console.log(
      monthYear,
      transactionDate,
      transactionType,
      fromAccount,
      toAccount,
      transactionAmount,
      receipt,
      notes
    );

    let insertData = {
      monthYear: {
        value: monthYear,
      },
      transactionDate: {
