    //     regData["id"] = 1;
    //     localStorage.setItem("registration", JSON.stringify([regData]));
    //   }
    //   navigate("/login");
    // } else {
    //   console.log("error existed");
    // }
  };


  useEffect(()=>{
if (Object.keys(error).length === 0 && issubmit) {
  let flag = false;
  // for (const key in data) {
  //   if (
  //     data[key].email === foValues.email &&
  //     data[key].password === foValues.password
  //   ) {
  //     flag = true;
  //     break;
  //   }
  // }
   for (const key in registrationData) {
     if (
       registrationData[key].email === loginData.email ||
       registrationData[key].password === loginData.password
     ) {
      flag=true;
     break;
     }
   }
  if (flag === true) {
    let result = "";
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const charactersLength = characters.length;
    let counter = 0;
    while (counter < 17) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
      counter += 1;
    }

    loginData["token"] = result;

    localStorage.setItem("logindata", JSON.stringify(loginData));

    navigate("/transection");
  }
}
  },[error])
  return (
    <>
      <div className="form">
        <h2>Login Form</h2>
        <form onSubmit={submitHandler} method="POST">
          <label className="label">Email:</label>
          <input
            type="email"
            name="email"
            className="inputFields"
            value={loginData.email}
            onChange={(e) => hasChange(e)}
          ></input>
          <span>{error.email}</span>
          <br></br>
          <label className="label">Password:</label>
          <input
            type="password"
            name="password"
            className="pswFields"
            value={loginData.password}
            onChange={(e) => hasChange(e)}
          ></input>
          <span>{error.password}</span>
          <span>{error.login}</span>
          <br></br>
          <button type="submit" className="ViewBtn">
            Submit
          </button>
          <br></br>
        </form>
        <Link to={"/create"} className="loginBtn">
          Login
        </Link>
      </div>
    </>
  );
};

export default Login;
