import './Pagination.css'
import React from 'react';

const Pagination = ({
  totalPosts,
  postsPerPage,
  setCurrentPage,
  currentPage,
  lastPostIndex,
firstPostIndex}) => {
  let pages = [];
  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    pages.push(i);
  }
  return (
    <>
      <div className="pagination">
        <button
          className= "page"
          onClick={() => {
            setCurrentPage();
          }}
        >
         Prev
        </button>

        {pages.map((page, index) => {
          return (
            <button
              key={index}
              className={page == currentPage ? "active" : "page"}
              onClick={() => {
                setCurrentPage(page);
              }}
            >
              {page}
            </button>
          );
        })}



        <button
          className= "page"
          onClick={() => {
            setCurrentPage();
          }}
        >
         Next
        </button>
     </div>
    </>
  );
};

export default Pagination;
