import {React,useState} from 'react';
import * as yup from "yup";
import {useForm} from 'react-hook-form'




const YupValidations = () => {
  const formSchema = yup.object().shape({
    email: yup.string().email().required(),
    password: yup.string().min(5).max(13).required(),
  });

  const[register,submitHandler] = useForm();
  const onSubmit = (e)=>{
    e.preventDefault();
    console.log(e.target);
    console.log("meet")
  }

  return (
    <>
      <form className="form" method='post' onSubmit={submitHandler(onSubmit)}>
        <label className="label">Name:</label>
        <input type="name" name="name" placeholder='enter name'/>
        <br></br>
        <label className="label">Email:</label>
        <input type="email" name="email" placeholder='enter email id' /> <br></br>
        <label className="label">Password:</label>
        <input type="password" name="password" placeholder='enter password' /> <br></br>
        <br></br>
        <button type="submit" className="submitBtn">
          Submit
        </button>
      </form>
    </>
  );
};

export default YupValidations;
