import "./Form.css";
import React, { useState } from "react";
import { useEffect } from "react";

// const getTransactionDetails = () => {
//   const storeDetails = localStorage.getItem("transactionForm");
//   if (!storeDetails)
//     return {
//       transactionDate: "",
//       monthYear: "",
//       transactionType: "",
//       fromAccount: "",
//       toAccount: "",
//       transactionAmount: "",
//       receipt: "",
//       notes: "",
//     };
//   return JSON.stringify(storeDetails);
// };
const TransactonForm = () => {
  const [details, setDetails] = useState([
    {
      transactionDate: "",
      monthYear: "",
      transactionType: "",
      fromAccount: "",
      toAccount: "",
      transactionAmount: "",
      receipt: "",
      notes: "",
    },
  ]);

  const [error, setError] = useState([]);

  // useEffect(() => {
  //   localStorage.setItem("transactionForm", JSON.stringify(details));
  // }, [details]);

  //   const hasChange = (e) => {
  //     const { name, value } = e.target;
  //     console.log(name, value);
  //     setDetails(
  //       { ...details, [name]: value }
  // //       (prev)=>{
  // // return( {...prev,[name]:value})
  // //     }
  //     );
  //   };

  // const hasChange =(e)=>{
  //   const {name,value} = e.target;
  //   setDetails((prev)=>{
  //   return {...prev, [name]:value}
  // })
  // }
  // onchange functions

  const DateHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const MonthHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const TypeHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const FromActHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const toActHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const AmountHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const ReceiptHnadler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const NotesHnadler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  console.log(details);

  const submitHandler = (e) => {
    e.preventDefault();
    console.log(e.target);
    localStorage.setItem("transactionForm", JSON.stringify(details));
  };

  const MonthArr = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  return (
    <>
      <div className="form">
        <form onSubmit={submitHandler} method="POST">
          <label className="label">Transaction Date:</label>
          <div className="input">
            <input
              type="date"
              name="transactionDate"
              onChange={DateHandler}
            ></input>
          </div>
          <br></br>
          <label className="label">Month Year:</label>

          <div className="input">
            <select name="monthYear" id="" onChange={MonthHandler}>
              <option value="selct" disabled selected>
                Select
              </option>

              {MonthArr.map((month) => {
                return (
                  <option value={`${month} 2023`}>{`${month} 2023`}</option>
                );
              })}

              {/* <option value="Jan 2023">Jan 2023</option>
              <option value="Feb 2023">Feb 2023</option>
              <option value="March 2023">March 2023</option>
              <option value="April 2023">April 2023</option>
              <option value="May 2023">May 2023</option>
              <option value="June 2023">June 2023</option>
              <option value="July 2023">July 2023</option>
              <option value="Aug 2023">Aug 2023</option>
              <option value="Sep 2023">Sep 2023</option>
              <option value="Oct 2023">Oct 2023</option>
              <option value="Nov 2023">Nov 2023</option>
              <option value="Dec 2023">Dec 2023</option> */}
            </select>
          </div>
          <br></br>
          <label className="label">Transaction Type:</label>
          <div className="input">
            <select name="transactionType" onChange={TypeHandler}>
              <option value="select" disabled selected>
                Select
              </option>
              <option value="Home Expense">Home Expense</option>
              <option value="Personal Expense">Personal Expense</option>
              <option value="Income">Income</option>
            </select>
          </div>
          <br></br>

          <label className="label">From Account:</label>
          <div className="input">
            <select name="fromAccount" onChange={FromActHandler}>
              <option value="select" disabled selected>
                Select
              </option>
              <option value="Personal Account"> Personal Account</option>
              <option value="Real Living">Real Living</option>
              <option value="My Dream Home">My Dream Home</option>
              <option value="Full Circle">Full Circle</option>
              <option value="Core Realtors">Core Realtors</option>
              <option value="Big Block">Big Block</option>
            </select>
          </div>
          <br></br>

          <label className="label">To Account: </label>
          <div className="input">
            <select name="toAccount" onChange={toActHandler}>
              <option value="select" disabled selected>
                Select
              </option>
              <option value="Personal Account"> Personal Account</option>
              <option value="Real Living">Real Living</option>
              <option value="My Dream Home">My Dream Home</option>
              <option value="Full Circle">Full Circle</option>
              <option value="Core Realtors">Core Realtors</option>
              <option value="Big Block">Big Block</option>
            </select>
          </div>
          <br></br>

          <label className="label">Amount:</label>
          <div className="input">
            <input
              type="text"
              name="transactionAmount"
              onChange={AmountHandler}
            ></input>
          </div>
          <br></br>
          <label className="label">Receipt:</label>
          <div className="input">
            <input
              type="file"
              name="receipt"
              alt="Receipt is not found"
              onChange={ReceiptHnadler}
            ></input>
          </div>
          <br></br>
          <label className="label">Notes:</label>
          <div className="input">
            <textarea
              cols="30"
              rows="6"
              name="notes"
              onChange={NotesHnadler}
            ></textarea>
          </div>

          <button type="submit" className="submitBtn">
            Submit
          </button>
        </form>
      </div>
    </>
  );
};

export default TransactonForm;
