      console.log(months.indexOf("January 2023"));
      sortedData.sort((a, b) => {
        return (
          months.indexOf(a[sortedField?.key]?.value) -
          months.indexOf(b[sortedField?.key]?.value)
        );
      });
    } else if (sortedField.direction === "descending") {
      sortedData.sort((a, b) => {
        return (
          months.indexOf(b[sortedField?.key]?.value) -
          months.indexOf(a[sortedField?.key]?.value)
        );
      });
    }
  } else {
