import { Fragment } from "react";
import "./Transaction.css";
import { Link } from "react-router-dom";
import { useState } from "react";
const Transaction = () => {
  const retrivedata = JSON.parse(localStorage.getItem("transactionForm"));
  const [order, setOrder] = useState({ keyToSort: "", direction: "asc" });
  // const [data, setData] = useState(retrivedata);
  // console.log(data, "last");

  // console.log(retrivedata);
  // retrivedata.map((transaction) => {
  //             // console.log(Object.values(transaction));
  //             console.log(":::")
  //             Object.values(transaction).map((aa) => {

  //             console.log(aa.value);

  //             });
  //             console.log(transaction)

  //               console.log(":::");
  //           })

  const sorting = (cols) => {
    // const col = cols;
    // console.log(col,"col name")

    // if (order === "Asc") {
    //   const sorted = [...data].sort((a, b) => (a[cols] > b[cols] ? 1 : -1));
    //   setData(sorted);
    //   setOrder("Des");
    //   console.log(sorted,"asc")
    // }
    // if (order === "Des") {
    //   const sorted = [...data].sort((a, b) => (a[cols] < b[cols] ? 1 : -1));
    //   setData(sorted);
    //   setOrder("Acs");
    //     console.log(sorted, "desc");
    // }

    setOrder({
      keyToSort: cols,
      direction:
        cols === order.cols
          ? order.direction === "asc"
            ? "desc"
            : "asc"
          : "desc",
    });
  };

  const sortedData = (dataToSort) => {
    if (order.direction === "asc") {
      return dataToSort.sort((a, b) =>
        a[order.keyToSort] > b[order.keyToSort] ? 1 : -1
      );
    }
    return dataToSort.sort((a, b) =>
      a[order.keyToSort] > b[order.keyToSort] ? -1 : 1
    );
  };

  return (
    <>
      <div className="details">
        <table>
          <thead>
            <th
            // onClick={() => {
            //   sorting("date");
            // }}
            >
              Transaction Date
            </th>
            <th
            // onClick={() => {
            //   sorting("month");
            // }}
            >
              Month Year
            </th>
            <th
              onClick={() => {
                sorting("transactionType");
              }}
            >
              Transaction Type
            </th>
            <th
            // onClick={() => {
            //   sorting("from");
            // }}
            >
              From Account
            </th>
            <th
            // onClick={() => {
            //   sorting("to");
            // }}
            >
              To Account
            </th>
            <th
            // onClick={() => {
            //   sorting("amount");
            // }}
            >
              Amount
            </th>
            <th>Receipt</th>
            <th
              onClick={() => {
                sorting("notes");
              }}
            >
              Notes
            </th>
            <th>Action</th>
          </thead>
          <tbody>
            {sortedData(retrivedata).map((transaction) => {
              return (
                <tr>
                  <td>{transaction.transactionDate.value}</td>
                  <td>{transaction.monthYear.value}</td>
                  <td>{transaction.transactionType.value}</td>
                  <td>{transaction.fromAccount.value}</td>
                  <td>{transaction.toAccount.value}</td>
                  <td>&#8377; {transaction.transactionAmount.value}</td>
                  <td>
                    <img
                      src={transaction.receipt.value}
                      width={50}
                      height={50}
                      alt=""
                    />
                  </td>
                  <td>{transaction.notes.value}</td>
                  <td>
                    <a href="#">View</a>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <button className="createBtn">
        <Link to={"/"}>Create Transaction</Link>{" "}
      </button>
    </>
  );
};
export default Transaction;
