import "./Form.css";
import React, { useState } from "react";
import { useEffect } from "react";

// const getTransactionDetails = () => {
//   const storeDetails = localStorage.getItem("transactionForm");
//   if (!storeDetails)
//     return {
//       transactionDate: "",
//       monthYear: "",
//       transactionType: "",
//       fromAccount: "",
//       toAccount: "",
//       transactionAmount: "",
//       receipt: "",
//       notes: "",
//     };
//   return JSON.stringify(storeDetails);
// };
const TransactonForm = () => {
  const [details, setDetails] = useState({
    transactionDate: "",
    monthYear: "",
    transactionType: "",
    fromAccount: "",
    toAccount: "",
    transactionAmount: "",
    receipt: "",
    notes: "",
  });

  const [error, setError] = useState({
    transactionDate: "",
    monthYear: "",
    transactionType: "",
    fromAccount: "",
    toAccount: "",
    transactionAmount: "",
    receipt: "",
    notes: "",
  });

  const formDetails = {
    amount: {
      value: "",
      error: "",
    },
  };

  const [data, setData] = useState(formDetails);

  // useEffect(() => {
  //   localStorage.setItem("transactionForm", JSON.stringify(details));
  // }, [details]);

  //   const hasChange = (e) => {
  //     const { name, value } = e.target;
  //     console.log(name, value);
  //     setDetails(
  //       { ...details, [name]: value }
  // //       (prev)=>{
  // // return( {...prev,[name]:value})
  // //     }
  //     );
  //   };

  // const hasChange =(e)=>{
  //   const {name,value} = e.target;
  //   setDetails((prev)=>{
  //   return {...prev, [name]:value}
  // })
  // }
  // onchange functions

  const DateHandler = (e) => {
    const { name, value } = e.target;
    console.log(name, value, "fdfgdfhg");

    if (!value) {
      // console.log("no transaction");
      setError((prev) => {
        return { ...prev, [name]: "Date is Required!!" };
      });
    } else {
      setError((prev) => {
        return { ...prev, [name]: " " };
      });
      console.log("yes");
    }
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const MonthHandler = (e) => {
    const { name, value } = e.target;

    if (value === "select") {
      console.log("no month");
      setError((prev) => {
        return { ...prev, [name]: "Month is Required!!" };
      });
    } else {
      setError((prev) => {
        return { ...prev, [name]: " " };
      });
      console.log("yes month");
    }
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const TypeHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const FromActHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const toActHandler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const AmountHandler = (e) => {
    const value = e ??  e.target.value;
    console.log(value,"amount to")
    setData((prev) => ({
      ...prev,
      amount: {
        ...prev.amount,
        value: value,
      },
    }));
    if (value) {
      if (value.length < 4) {
        setData((prev) => ({
          ...prev,
          amount: {
            ...prev.amount,
            error: "Amount is too short!!",
          },
        }));
      } else {
        setData((prev) => ({
          ...prev,
          amount: {
            ...prev.amount,
            error: "",
          },
        }));
        // console.log("highest value");
      }
    } else {
      setData((prev) => ({
        ...prev,
        amount: {
          ...prev.amount,
          error: "amount is not entered",
        },
      }));
    }

    console.log(value);
    // const { name, value } = e.target;
    // setDetails((prev) => {
    //   return { ...prev, [name]: value };
    // });
  };

  const ReceiptHnadler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const NotesHnadler = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  // console.log(details);
  console.log(data);
  const submitHandler = (e) => {
    e.preventDefault();
    // DateHandler(e);
    // console.log(data.amount.value);
    AmountHandler(data.amount.value);
    // console.log(data.amount.error === "");
    // data.amount.error ? console.log("error exists") : console.log("not exists");

    //  localStorage.setItem("transactionForm", JSON.stringify(details));

    if (localStorage.getItem("transactionForm")) {
      const retrivedata = JSON.parse(localStorage.getItem("transactionForm"));

      retrivedata.push(details);

      localStorage.setItem("transactionForm", JSON.stringify(retrivedata));
    } else {
      localStorage.setItem("transactionForm", JSON.stringify([details]));
    }
  };

  const MonthArr = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const TransactionTypeArr = ["Home Expense", "Personal Expense", "Income"];
  const AccountArr = [
    "Personal Account",
    "Real Living",
    "My Dream Home",
    "Full Circle",
    "Core Realtors",
    "Big Block",
  ];

  return (
    <>
      <div className="form">
        <form onSubmit={submitHandler} method="POST">
          <label className="label">Transaction Date:</label>
          <div className="input">
            <input
              type="date"
              name="transactionDate"
              onChange={DateHandler}
            ></input>
          </div>
          <br></br>
          <label className="label">Month Year:</label>

          <div className="input">
            <select name="monthYear" id="" onChange={MonthHandler}>
              <option value="select" disabled selected>
                Select
              </option>

              {MonthArr.map((month) => {
                return (
                  <option value={`${month} 2023`}>{`${month} 2023`}</option>
                );
              })}

              {/* <option value="Jan 2023">Jan 2023</option>
                <option value="Feb 2023">Feb 2023</option>
                */}
            </select>
          </div>
          <br></br>
          <label className="label">Transaction Type:</label>
          <div className="input">
            <select name="transactionType" onChange={TypeHandler}>
              <option value="select" disabled selected>
                Select
              </option>
              {TransactionTypeArr.map((type) => {
                return <option value={`${type}`}>{type}</option>;
              })}
            </select>
          </div>
          <br></br>

          <label className="label">From Account:</label>
          <div className="input">
            <select name="fromAccount" onChange={FromActHandler}>
              <option value="select" disabled selected>
                Select
              </option>
              {AccountArr.map((account) => {
                return <option value={account}>{account}</option>;
              })}
            </select>
          </div>
          <br></br>

          <label className="label">To Account: </label>
          <div className="input">
            <select name="toAccount" onChange={toActHandler}>
              <option value="select" disabled selected>
                Select
              </option>

              {AccountArr.map((account) => {
                return <option value={account}>{account}</option>;
              })}
            </select>
          </div>
          <br></br>
          <label className="label">Amount:</label>
          <div className="input">
            <input
              type="text"
              name="transactionAmount"
              value={data.amount.value}
              onChange={AmountHandler}
            ></input>
          </div>
          <br></br>
          <label className="label">Receipt:</label>
          <div className="input">
            <input
              type="file"
              name="receipt"
              alt="Receipt is not found"
              onChange={ReceiptHnadler}
            ></input>
          </div>
          <br></br>
          <label className="label">Notes:</label>
          <div className="input">
            <textarea
              cols="30"
              rows="6"
              name="notes"
