const TransactonForm = () => {
  const [details, setDetails] = useState({
    transactionDate: "",
    monthYear: "",
    transactionType: "",
    fromAccount: "",
    toAccount: "",
    transactionAmount: "",
    receipt: "",
    notes: "",
  });

  const [error, setError] = useState(
    {
      transactionDate: "",
      monthYear: "",
      transactionType: "",
      fromAccount: "",
      toAccount: "",
      transactionAmount: "",
      receipt: "",
      notes: "",
    },
  );
const [data,setData]=useState({
  amount:{
